  const [showHTML , setShowHTMl] = useState(false);
  const [questionsArray , setQuestionsArray] = useState([
    {
      serialNo: "1",
      question: "Is this a public seafarer recruitment and placement service?",
      num: "1",
    },
    {
      serialNo: "2",
      question: "Is this a private seafarer recruitment and placement service?",
      num: "2",
    },
    {
      serialNo: "3",
      question:
        "Does the SRPS use means, mechanisms or lists that are intended to prevent or deter seafarers’ from gaining employment for which they are qualified for? (not permitted)",
      num: "3",
    },
  ])
  return (
    <div className="App">
      <DataCreator/>
      <button onClick={()=>setShowHTMl(value => !value)}>{`${showHTML === false ? "hide" : "Show"}`} html structure</button>
      {showHTML && <Formbuild /> }
    </div>
  );
}

export default App;
