import React from "react";
import { useState, useRef } from "react";
import "./dataCreator.css";

function DataCreator() {
  const [questionList, setQuestionList] = useState([]);
  const [serialNo, setSerialNo] = useState("");
  const [question, setQuestion] = useState("");
  const [num, setNum] = useState(1);
  const [extraField, setExtraField] = useState("");

  function deleteQuestionFunction(id){
    console.log(questionList,"questionLIst")
    const currentElement = questionList.find(item => item.num === id)
    console.log(currentElement,"currentElement")
  }
  return (
    <div className="border-grey p-30 mb-20">
      <input
        className="border-one p-3 mr-10"
        placeholder="Enter serial no."
        value={serialNo}
        onChange={(e) => setSerialNo(e.target.value)}
      />
      <input
        className="w-100 border-one p-3 mr-10"
        placeholder="Enter question"
        value={question}
        onChange={(e) => setQuestion(e.target.value)}
      />
      <input 
        className="w-100 border-one p-3 mr-10"
        placeholder="Enter if any extra fields"
        value={extraField}
        onChange={(e) => setExtraField(e.target.value)}
      />
      <button
        className="p-10 border-none"
        onClick={() => {
          setNum((prev) => prev + 1);
          setQuestionList([
            ...questionList,
            {
              serialNo: `"${serialNo}"`,
              question: `"${question}"`,
              num: `"${num}"`,
              extraField:`"${extraField}"`
            },
          ]);
          setSerialNo("");
          setQuestion("");
          setExtraField("")
        }}
      >
        Add question
      </button>
        <button className="p-10 border-none" onClick={()=>deleteQuestionFunction(num)}>
            Delete question
        </button>
      <br />
      <div className="mt-15">DATA STRUCTURE IN OBJECT FORMAT</div>
      <div className="border-grey p-30 mb-20">
        {questionList.map((item) => {
          return (
            <>
              {` {
            serialNo : ${item.serialNo} , 
            question :${item.question},
            num :${item.num},
            extraField: ${item.extraField}
        },`}
              <br />
            </>
          );
        })}
      </div>
    </div>
  );
}

export default DataCreator;
